mapper.insert:
insert into medi_member(
membernum
, memberid
, memberpw
, membername

	, email


	, phone


	, address


	, history


	, allergy


	, medicine

)
values (
medi_member_seq.nextval
, #{memberid}
, #{memberpw}
, #{membername}

	, #{email}


	, #{phone}


	, #{address}


	, #{history}


	, #{allergy}


	, #{medicine}

)

mapper.update:
update medi_member set
mamberpw = #{memberpw}
, membername = #{membername}
, email = #{email}
, phone = #{phone}
, address = #{address}
where
membernum = #{membernum}
