mapper.select:
select m.mednum, m.medname, m.medco, m.price, m.takemed, m.ingredient, m.note, m.picture, s.signpart, s.signfirst, s.signsecond,
(select decode(avg(evaluate), null, 2.5, avg(evaluate)) from review where mednum = m.mednum) 
as star from medicine m left join show_sign s on m.mednum = s.mednum where m.mednum = #{mednum}

mapper.insert:
insert into medicine
	mednum
	, medname
	, medco
	, price

	, takemed

	, medcycle

	, ingredient

	, note
values (
	 medicine_seq.nextval
	, #{medname}
	, #{medco}
	, #{price}

	, #{takemed}

	, #{medcycle}

	, #{ingredient}

	, #{note}
)

mapper.update:
update medicine set
	medname = #{medname}
	, medco = #{medco}
	, price = #{price}

	, takemed = #{takemed}

	, medcycle = #{medcycle}

	, ingredient = #{ingredient}

	, note = #{note}
where
	mednum = #{mednum}
