mapper.insert:
insert into medicine
	mednum
	, medname
	, medco
	, price

	, takemed

	, medcycle

	, ingredient

	, note
values (
	 medicine_seq.nextval
	, #{medname}
	, #{medco}
	, #{price}

	, #{takemed}

	, #{medcycle}

	, #{ingredient}

	, #{note}
)

mapper.update:
update medicine set
	medname = #{medname}
	, medco = #{medco}
	, price = #{price}

	, takemed = #{takemed}

	, medcycle = #{medcycle}

	, ingredient = #{ingredient}

	, note = #{note}
where
	mednum = #{mednum}
